        >
          {{ state.thisPageRequiresAuthenticatedUser ? 'Disable' : 'Enable' }} authentication
          requirement.. Currently: {{ state.thisPageRequiresAuthenticatedUser }}
        </button>
      </div>
    </AlgorandAuthentication>
